template.script:
$(document).ready(function() {
    $('#server-request').click(function() {
        $( "#server-response" ).empty();
        var textValue = $('#text').val();
        $.ajax({
            url: "/comics/api",
            method: 'GET',
            data: {text: textValue},
            success: function( result ) {
                $( "#server-response" ).html(result);
            }
        });
    });
});
