t.script:
                    $(document).ready(function() {
                        $('#server-request').click(function() {
                        $( "#tables-body" ).empty();
                        var textValue = $('#text').val();

                        $.ajax({
                            url: "/comics/api",
                            //async: false,
                            method: 'GET',
                            data: {text: textValue},
                            success: function (response) {
                                var result = $.parseJSON(response);
                                console.log(result);
                                $.each(result.data.results, function (i, item) {
                                    var thumbnail = $('');
                                    thumbnail.attr('src', item.thumbnail.path + "." + item.thumbnail.extension);
                                    thumbnail.attr('alt', "No image found");
                                    thumbnail.attr('height', 50);
                                    var tr_row = $("
");
                                    tr_row.append($('').html(item.title))
                                        .append($('').html(item.format))
                                        .append($('').html( item.dates[0].date.slice(0, 10)))
                                        .append($('').html(item.ean))
                                        .append($('').html(thumbnail[0].outerHTML));
                                    $('#tables-body').append(tr_row);
                                    tr_row.bind('click', {info: item}, fullInfo);
                                });
                            },
                            timeout: 8000,
                            error: function(){
                                $('#tables-body').html('Please try again');
                            }
                        })

                    });

                    fullInfo = function(event){
                        console.log('on_Click', event.data.info);
                    }
                    });
